import { Injectable } from '@nestjs/common'
import { Item } from 'src/items/interface/items.interface'
import { ItemsService } from 'src/items/items.service'
import { MenuDto } from './dto/menu.dto'
import { Menu } from './interfaces/menu.interface'
import { Menu as MenuEntity } from './entity/menu.entity'
import { InjectRepository } from '@nestjs/typeorm'
import { Repository } from 'typeorm'

@Injectable()
export class MenuService {
    constructor(
        private readonly itemsService: ItemsService,
        @InjectRepository(MenuEntity)
        private menuRepository: Repository<MenuEntity>
    ) {}
    private readonly menus: Menu[] = [
        {
            id: 1,
            name: 'lunch Menu',
            description: 'lunch Menu',
            image: 'data:image/jpeg;base64,',
        },
        {
            id: 2,
            name: 'Breakfast Menu',
            description: 'Breakfast Menu',
            image: 'data:image/jpeg;base64,',
        },
    ]

    create(menu: MenuDto): Promise<Menu> {
        const menuEntity: MenuEntity = this.menuRepository.create(menu)
        return this.menuRepository.save(menuEntity)
    }

    findAll(): Promise<Menu[]> {
        return this.menuRepository.find()
    }

    findOne(id: number): Promise<Menu> {
        return this.menuRepository.findOne({ id })
    }

    findItems(id: number): Promise<Item[]> {
        return this.itemsService.find(id)
    }
}
